import React, { useState, useEffect, useCallback } from 'react';

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    // Initialize dark mode from localStorage or system preference
    if (typeof window !== 'undefined') {
      const stored = localStorage.getItem('marketMoodDarkMode');
      if (stored !== null) {
        return stored === 'true';
      }
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });

  const [headlines, setHeadlines] = useState([]);
  const [moodScore, setMoodScore] = useState(0);
  const [sentimentCounts, setSentimentCounts] = useState({ bullish: 0, bearish: 0, neutral: 0 });
  const [lastUpdated, setLastUpdated] = useState('');
  const [loading, setLoading] = useState(true);

  // Expanded sample headlines to reach at least 20
  const sampleHeadlinesData = [
    { title: 'Sensex surges 500 points on positive quarterly earnings', source: 'Economic Times', url: '#', sentiment_score: 0.7, sentiment_label: 'Bullish' },
    { title: 'Nifty touches new all-time high amid strong FII inflows', source: 'Business Standard', url: '#', sentiment_score: 0.8, sentiment_label: 'Bullish' },
    { title: 'Banking stocks decline on concerns over loan growth slowdown', source: 'MoneyControl', url: '#', sentiment_score: -0.6, sentiment_label: 'Bearish' },
    { title: 'IT sector shows mixed performance in Q3 results, uncertainty remains', source: 'Reuters India', url: '#', sentiment_score: 0.1, sentiment_label: 'Neutral' },
    { title: 'Rupee strengthens against dollar as global risk appetite improves', source: 'LiveMint', url: '#', sentiment_score: 0.5, sentiment_label: 'Bullish' },
    { title: 'Auto stocks rally on strong festive season sales numbers', source: 'Financial Express', url: '#', sentiment_score: 0.65, sentiment_label: 'Bullish' },
    { title: 'Pharma sector faces regulatory headwinds in key export markets', source: 'The Hindu BusinessLine', url: '#', sentiment_score: -0.45, sentiment_label: 'Bearish' },
    { title: 'Metal stocks gain as global commodity prices see an uptick', source: 'CNBC TV18', url: '#', sentiment_score: 0.4, sentiment_label: 'Bullish' },
    { title: 'FMCG companies report resilient demand despite inflationary pressures', source: 'Times of India', url: '#', sentiment_score: 0.3, sentiment_label: 'Neutral' },
    { title: 'Real estate sector sees continued momentum in housing sales', source: 'NDTV Profit', url: '#', sentiment_score: 0.55, sentiment_label: 'Bullish' },
    { title: 'Global cues weak, Asian markets trade lower on inflation fears', source: 'Bloomberg Quint', url: '#', sentiment_score: -0.7, sentiment_label: 'Bearish' },
    { title: 'Government announces new PLI scheme for manufacturing boost', source: 'PIB India', url: '#', sentiment_score: 0.75, sentiment_label: 'Bullish' },
    { title: 'Oil prices surge past $90/barrel, stoking inflation worries', source: 'Reuters Global', url: '#', sentiment_score: -0.65, sentiment_label: 'Bearish' },
    { title: 'Startup funding sees a slight dip in the last quarter', source: 'TechCrunch India', url: '#', sentiment_score: -0.2, sentiment_label: 'Neutral' },
    { title: 'RBI MPC likely to maintain status quo on interest rates', source: 'Economic Times Poll', url: '#', sentiment_score: 0.0, sentiment_label: 'Neutral' },
    { title: 'Infrastructure spending set to increase in upcoming budget', source: 'Business Today', url: '#', sentiment_score: 0.6, sentiment_label: 'Bullish' },
    { title: 'Retail inflation eases slightly but remains above comfort zone', source: 'MoSPI Data', url: '#', sentiment_score: -0.1, sentiment_label: 'Neutral' },
    { title: 'Semiconductor shortage continues to impact auto production', source: 'Automotive News', url: '#', sentiment_score: -0.5, sentiment_label: 'Bearish' },
    { title: 'Green energy stocks rally on new government policies', source: 'CleanTechnica', url: '#', sentiment_score: 0.85, sentiment_label: 'Bullish' },
    { title: 'Indian exports show robust growth for the third consecutive month', source: 'Ministry of Commerce', url: '#', sentiment_score: 0.7, sentiment_label: 'Bullish' },
    { title: 'Market volatility expected to continue in the short term', source: 'Market Analysts Inc.', url: '#', sentiment_score: -0.3, sentiment_label: 'Bearish' },
    { title: 'Tech giants invest heavily in Indian AI and Cloud infrastructure', source: 'Economic Times Tech', url: '#', sentiment_score: 0.6, sentiment_label: 'Bullish' },
  ];

  // Apply or remove 'dark' class on HTML element when isDarkMode changes
  useEffect(() => {
    const htmlElement = document.documentElement;
    if (isDarkMode) {
      htmlElement.classList.add('dark');
    } else {
      htmlElement.classList.remove('dark');
    }
    try {
      localStorage.setItem('marketMoodDarkMode', isDarkMode.toString());
    } catch (error) {
      console.warn('Could not save dark mode preference to localStorage:', error);
    }
  }, [isDarkMode]);

  const calculateMoodScore = useCallback((headlinesData) => {
    if (!headlinesData || headlinesData.length === 0) {
      setSentimentCounts({ bullish: 0, bearish: 0, neutral: 0 });
      return 0;
    }

    let bullishCount = 0;
    let bearishCount = 0;
    let neutralCount = 0;

    headlinesData.forEach((h) => {
      if (h.sentiment_label === 'Bullish') bullishCount++;
      else if (h.sentiment_label === 'Bearish') bearishCount++;
      else neutralCount++;
    });

    const total = headlinesData.length;
    const score = total > 0 ? ((bullishCount - bearishCount) / total) * 100 : 0;

    setSentimentCounts({ bullish: bullishCount, bearish: bearishCount, neutral: neutralCount });
    return Math.round(score); // Keep as integer for gauge simplicity
  }, []);

  const loadData = useCallback(async () => {
    setLoading(true);
    // Simulate API delay
    await new Promise((resolve) => setTimeout(resolve, 1200));

    try {
      // In a real app, you would fetch this data from an API
      const headlinesData = sampleHeadlinesData.sort(() => 0.5 - Math.random()); // Shuffle for variety on refresh
      const calculatedMoodScore = calculateMoodScore(headlinesData);

      setHeadlines(headlinesData);
      setMoodScore(calculatedMoodScore);
      setLastUpdated(new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
    } catch (error) {
      console.error('Error loading data:', error);
      // Fallback or error handling
    } finally {
      setLoading(false);
    }
  }, [calculateMoodScore]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  const toggleDarkMode = () => {
    setIsDarkMode(prev => !prev);
  };

  const getSentimentStyles = (label) => {
    switch (label) {
      case 'Bullish':
        return {
          bg: 'bg-green-100 dark:bg-green-900 dark:bg-opacity-30',
          text: 'text-green-700 dark:text-green-400',
          border: 'border-green-500 dark:border-green-600',
          tagBg: 'bg-green-200 dark:bg-green-700',
          tagText: 'text-green-800 dark:text-green-200',
        };
      case 'Bearish':
        return {
          bg: 'bg-red-100 dark:bg-red-900 dark:bg-opacity-30',
          text: 'text-red-700 dark:text-red-400',
          border: 'border-red-500 dark:border-red-600',
          tagBg: 'bg-red-200 dark:bg-red-700',
          tagText: 'text-red-800 dark:text-red-200',
        };
      default: // Neutral
        return {
          bg: 'bg-gray-100 dark:bg-gray-700 dark:bg-opacity-30',
          text: 'text-gray-700 dark:text-gray-400',
          border: 'border-gray-400 dark:border-gray-500',
          tagBg: 'bg-gray-200 dark:bg-gray-600',
          tagText: 'text-gray-800 dark:text-gray-200',
        };
    }
  };

  const GaugeChart = ({ score }) => {
    const normalizedScore = Math.max(-100, Math.min(100, score)); // Ensure score is within -100 to 100
    // Map score from [-100, 100] to an angle [0, 180] degrees.
    // -100 maps to 0 deg (left), 0 maps to 90 deg (middle), 100 maps to 180 deg (right)
    const angle = ((normalizedScore + 100) / 200) * 180;

    let moodText = 'Neutral';
    let moodTextColor = 'text-gray-600 dark:text-gray-400';
    let progressColor = isDarkMode ? 'rgb(75 85 99)' : 'rgb(107 114 128)'; // Gray for neutral

    if (score > 25) {
      moodText = 'Very Bullish';
      moodTextColor = 'text-green-600 dark:text-green-400';
      progressColor = 'rgb(16 185 129)'; // Green
    } else if (score > 0) {
      moodText = 'Bullish';
      moodTextColor = 'text-green-500 dark:text-green-400';
      progressColor = 'rgb(16 185 129)'; // Green
    } else if (score < -25) {
      moodText = 'Very Bearish';
      moodTextColor = 'text-red-600 dark:text-red-400';
      progressColor = 'rgb(239 68 68)'; // Red
    } else if (score < 0) {
      moodText = 'Bearish';
      moodTextColor = 'text-red-500 dark:text-red-400';
      progressColor = 'rgb(239 68 68)'; // Red
    }

    return (
      <div className="relative w-72 h-40 mx-auto"> {/* Increased size for better visual */}
        <svg viewBox="0 0 200 100" className="w-full h-full transform ">
          {/* Background Arc (Full Semi-Circle) */}
          <path
            d="M 20 90 A 70 70 0 0 1 180 90" // Start x,y | Arc radii | x-axis-rotation large-arc-flag sweep-flag | End x,y
            fill="none"
            stroke={isDarkMode ? '#374151' : '#e5e7eb'} // Darker gray for dark mode, lighter for light
            strokeWidth="20" // Thicker arc
            strokeLinecap="round"
          />
          {/* Progress Arc (Corrected - no dynamic path needed, just clip or segments) */}
          {/* This is a simplified approach. A true progress arc with variable end is complex in SVG path 'd' attr.
              We'll use a trick with stroke-dasharray and stroke-dashoffset if needed,
              or for simplicity, the needle does most of the visual work.
              Here, we just show the full possible range in progressColor if needed,
              but for this design, the needle is the primary indicator.
              Let's make the progress bar show a solid color based on mood.
          */}
          {score !== 0 && (
            <path
              d="M 20 90 A 70 70 0 0 1 180 90"
              fill="none"
              stroke={progressColor}
              strokeWidth="20"
              strokeLinecap="round"
              style={{
                strokeDasharray: Math.PI * 70, // Circumference of a full circle with r=70 is 2*PI*70. For semi-circle PI*70
                strokeDashoffset: Math.PI * 70 * (1 - (angle / 180)), // Offset based on angle
                transition: 'stroke-dashoffset 0.7s ease-out'
              }}
             />
          )}

          {/* Needle */}
          <line
            x1="100" // Center X of the arc's base
            y1="90"  // Center Y of the arc's base
            x2="100" // This will be calculated based on angle
            y2="20"  // Tip of the needle pointing upwards
            stroke={isDarkMode ? '#a0aec0' : '#4a5568'}
            strokeWidth="3"
            strokeLinecap="round"
            className="gauge-needle"
            style={{
              transformOrigin: '100px 90px', // SVG's coordinate system (center of rotation)
              transform: `rotate(${angle - 90}deg)`, // -90 to align 0 deg of needle to left
            }}
          />
          {/* Needle Hub */}
          <circle cx="100" cy="90" r="6" fill={isDarkMode ? '#cbd5e1' : '#2d3748'} />
        </svg>

        <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 text-center w-full">
          <div className={`text-4xl font-bold ${moodTextColor}`}>
            {score >= 0 ? `+${score}` : score}
          </div>
          <div className={`text-md font-medium ${moodTextColor} uppercase tracking-wider`}>
            {moodText}
          </div>
        </div>
      </div>
    );
  };

  const HeadlineCard = ({ headline }) => {
    const styles = getSentimentStyles(headline.sentiment_label);
    const sentimentEmoji =
      headline.sentiment_label === 'Bullish' ? 'üìà' :
      headline.sentiment_label === 'Bearish' ? 'üìâ' :
      '‚û°Ô∏è';

    return (
      <div
        className={`border-l-4 ${styles.border} ${isDarkMode ? 'bg-dark-card hover:bg-gray-700' : 'bg-light-card hover:bg-gray-50'} 
                    rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 group animate-fadeIn`}
      >
        <div className="flex justify-between items-start mb-3">
          <span className={`text-xs font-medium ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'}`}>
            {headline.source}
          </span>
          <span
            className={`text-xs font-semibold ${styles.tagText} ${styles.tagBg} px-2.5 py-1 rounded-full`}
          >
            {headline.sentiment_label}
          </span>
        </div>
        <h4 className="text-lg font-semibold mb-2 leading-snug group-hover:text-brand-primary dark:group-hover:text-blue-400 transition-colors duration-200">
          {headline.url && headline.url !== '#' ? (
            <a
              href={headline.url}
              target="_blank"
              rel="noopener noreferrer"
              className="focus:outline-none focus:ring-2 focus:ring-brand-primary dark:focus:ring-blue-500 rounded"
            >
              {headline.title}
            </a>
          ) : (
            headline.title
          )}
        </h4>
        <div className={`text-sm ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'} flex justify-between items-center`}>
          <span>Score: <span className={`font-bold ${styles.text}`}>{headline.sentiment_score}</span></span>
          <span className="text-2xl">{sentimentEmoji}</span>
        </div>
      </div>
    );
  };

  const SentimentStatCard = ({ icon, label, count, colorClass, bgColorClass }) => (
    <div className={`flex flex-col items-center justify-center p-5 rounded-xl shadow-md
                   ${isDarkMode ? 'bg-dark-card' : bgColorClass} 
                   w-full sm:w-auto min-w-[120px] transition-all duration-300`}>
      <span className="text-4xl mb-2">{icon}</span>
      <span className={`font-bold text-3xl mb-1 ${colorClass}`}>{count}</span>
      <span className={`text-sm font-medium ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'}`}>{label}</span>
    </div>
  );

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-dark-bg text-dark-text' : 'bg-light-bg text-light-text'}`}>
      {/* Header */}
      <nav className={`${isDarkMode ? 'bg-dark-card' : 'bg-light-card'} shadow-lg sticky top-0 z-50 border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            <div className="flex-shrink-0 flex items-center gap-3">
              <span className="text-4xl">üìä</span>
              <span className="text-3xl font-bold gradient-text bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500">
                Market Mood
              </span>
            </div>
            <div className="flex items-center gap-4">
              <span className={`text-sm ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'} hidden md:block`}>
                Last updated: {loading ? 'Updating...' : lastUpdated || 'N/A'}
              </span>
              <button
                onClick={toggleDarkMode}
                className={`p-2.5 rounded-full transition-all duration-300 focus:outline-none focus:ring-2
                            ${isDarkMode ? 'bg-gray-700 hover:bg-gray-600 text-yellow-400 focus:ring-yellow-500' 
                                         : 'bg-gray-200 hover:bg-gray-300 text-purple-600 focus:ring-purple-500'}`}
                title="Toggle Dark Mode"
              >
                {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
        {/* Mood Gauge Section */}
        <section className="mb-16 text-center">
          <h2 className="text-4xl font-extrabold mb-4 gradient-text bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600">
            Today's Market Pulse
          </h2>
