// src/App.js
import React, { useState, useEffect } from 'react';

const App = () => {
  // 1. Initialize isDarkMode from localStorage
  const [isDarkMode, setIsDarkMode] = useState(() => {
    try {
      const stored = localStorage.getItem('marketMoodDarkMode');
      return stored === 'true';
    } catch {
      return false;
    }
  });

  // Other state variables:
  const [headlines, setHeadlines] = useState([]);
  const [moodScore, setMoodScore] = useState(0);
  const [sentimentCounts, setSentimentCounts] = useState({ bullish: 0, bearish: 0, neutral: 0 });
  const [lastUpdated, setLastUpdated] = useState('');
  const [loading, setLoading] = useState(false);

  // Sample headlines — replace with real API calls later
  const sampleHeadlines = [
    {
      title: 'Sensex surges 500 points on positive quarterly earnings across sectors',
      source: 'Economic Times',
      url: 'https://economictimes.com',
      sentiment_score: 0.7,
      sentiment_label: 'Bullish',
    },
    {
      title: 'Nifty touches new all-time high amid strong FII inflows',
      source: 'Business Standard',
      url: 'https://business-standard.com',
      sentiment_score: 0.8,
      sentiment_label: 'Bullish',
    },
    {
      title: 'Banking stocks decline on concerns over loan growth',
      source: 'MoneyControl',
      url: 'https://moneycontrol.com',
      sentiment_score: -0.6,
      sentiment_label: 'Bearish',
    },
    {
      title: 'IT sector shows mixed performance in Q3 results',
      source: 'Economic Times',
      url: 'https://economictimes.com',
      sentiment_score: 0.1,
      sentiment_label: 'Neutral',
    },
    {
      title: 'Rupee strengthens against dollar on positive sentiment',
      source: 'Business Standard',
      url: 'https://business-standard.com',
      sentiment_score: 0.5,
      sentiment_label: 'Bullish',
    },
    {
      title: 'Auto stocks rally on strong sales numbers for December',
      source: 'MoneyControl',
      url: 'https://moneycontrol.com',
      sentiment_score: 0.6,
      sentiment_label: 'Bullish',
    },
    {
      title: 'Pharma sector faces regulatory challenges in key markets',
      source: 'Economic Times',
      url: 'https://economictimes.com',
      sentiment_score: -0.4,
      sentiment_label: 'Bearish',
    },
    {
      title: 'Metal stocks gain on global commodity price rise',
      source: 'Business Standard',
      url: 'https://business-standard.com',
      sentiment_score: 0.4,
      sentiment_label: 'Bullish',
    },
  ];

  // Calculate mood score and update sentiment count
  const calculateMoodScore = (headlinesData) => {
    if (!headlinesData || headlinesData.length === 0) return 0;

    const bullishCount = headlinesData.filter((h) => h.sentiment_label === 'Bullish').length;
    const bearishCount = headlinesData.filter((h) => h.sentiment_label === 'Bearish').length;
    const neutralCount = headlinesData.filter((h) => h.sentiment_label === 'Neutral').length;

    const total = headlinesData.length;
    const moodScore = total > 0 ? ((bullishCount - bearishCount) / total) * 100 : 0;

    setSentimentCounts({ bullish: bullishCount, bearish: bearishCount, neutral: neutralCount });
    return Math.round(moodScore * 10) / 10;
  };

  // Simulate loading data (e.g., could be replaced with an API call)
  const loadData = async () => {
    setLoading(true);
    await new Promise((resolve) => setTimeout(resolve, 1500));

    try {
      const headlinesData = sampleHeadlines;
      const calculatedMoodScore = calculateMoodScore(headlinesData);

      setHeadlines(headlinesData);
      setMoodScore(calculatedMoodScore);
      setLastUpdated(new Date().toLocaleString());
    } catch (error) {
      console.error('Error loading data:', error);
      alert('Failed to load data. Using sample data.');
    } finally {
      setLoading(false);
    }
  };

  // Toggle dark mode + persist to localStorage
  const toggleDarkMode = () => {
    setIsDarkMode((prev) => {
      const next = !prev;
      try {
        localStorage.setItem('marketMoodDarkMode', next.toString());
      } catch {
        // ignore if localStorage is unavailable
      }
      return next;
    });
  };

  useEffect(() => {
    loadData();
  }, []); // run once on mount

  // Determine colors based on sentiment label
  const getSentimentColor = (label) => {
    switch (label) {
      case 'Bullish':
        return { bg: 'bg-green-100', text: 'text-green-600', border: 'border-green-500' };
      case 'Bearish':
        return { bg: 'bg-red-100', text: 'text-red-600', border: 'border-red-500' };
      default:
        return { bg: 'bg-gray-100', text: 'text-gray-600', border: 'border-gray-500' };
    }
  };

  // Gauge chart component
const GaugeChart = ({ score }) => {
  const normalizedScore = Math.max(-100, Math.min(100, score));
  const angle = (normalizedScore + 100) * 0.9; // map -100…100 to 0…180°
  const rad = (180 - angle) * (Math.PI / 180);
  const x = 128 + Math.cos(rad) * 108;
  const y = 128 - Math.sin(rad) * 108;

  return (
    <div className="relative w-64 h-32 mx-auto overflow-hidden">
      <svg width="100%" height="100%" viewBox="0 0 256 128" className="block">
        {/* Background arc */}
        <path
          d="M 20 128 A 108 108 0 0 1 236 128"
          fill="none"
          stroke="#e5e7eb"
          strokeWidth="12"
          strokeLinecap="round"
        />

        {/* Progress arc */}
        <path
          d={`M 20 128 A 108 108 0 ${angle > 90 ? 1 : 0} 1 ${x} ${y}`}
          fill="none"
          stroke={score >= 0 ? '#10b981' : '#ef4444'}
          strokeWidth="12"
          strokeLinecap="round"
        />

        {/* Center dot */}
        <circle cx="128" cy="128" r="4" fill="#9ca3af" />
      </svg>

      {/* Score text */}
      <div className="absolute top-14 left-1/2 transform -translate-x-1/2 text-center">
        <div className="text-3xl font-bold">{score > 0 ? `+${score}` : `${score}`}</div>
        <div className="text-sm text-gray-500">
          {score > 20
            ? 'Very Bullish'
            : score > 0
            ? 'Bullish'
            : score < -20
            ? 'Very Bearish'
            : score < 0
            ? 'Bearish'
            : 'Neutral'}
        </div>
      </div>
    </div>
  );
};

  // Individual headline card
  const HeadlineCard = ({ headline }) => {
    const colors = getSentimentColor(headline.sentiment_label);

    return (
      <div
        className={`border-l-4 ${colors.border} ${
          isDarkMode ? 'bg-gray-800' : 'bg-white'
        } rounded-lg p-4 shadow-lg hover:shadow-xl transition-all duration-300`}
      >
        <div className="flex justify-between items-center mb-2">
          <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
            {headline.source}
          </span>
          <span
            className={`text-sm font-medium ${colors.text} ${colors.bg} ${
              isDarkMode ? 'bg-opacity-20' : ''
            } px-2 py-1 rounded-full`}
          >
            {headline.sentiment_label}
          </span>
        </div>
        <h4 className="text-md font-semibold mb-2 leading-tight">
          {headline.url ? (
            <a
              href={headline.url}
              target="_blank"
              rel="noopener noreferrer"
              className={`${
                isDarkMode ? 'hover:text-blue-400' : 'hover:text-blue-600'
              } transition-colors duration-200`}
            >
              {headline.title}
            </a>
          ) : (
            headline.title
          )}
        </h4>
        <div className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-400'} flex justify-between`}>
          <span>Score: {headline.sentiment_score}</span>
          <span className="text-lg">
            {headline.sentiment_label === 'Bullish' && '📈'}
            {headline.sentiment_label === 'Bearish' && '📉'}
            {headline.sentiment_label === 'Neutral' && '➡️'}
          </span>
        </div>
      </div>
    );
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${
      isDarkMode ? 'bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'
    }`}>
      {/* Header */}
      <nav className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg border-b ${
        isDarkMode ? 'border-gray-700' : 'border-gray-200'
      }`}>
        <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold flex items-center gap-3">
            <span className="text-3xl">📊</span>
            <span className="bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Market Mood
            </span>
          </h1>
          <div className="flex items-center gap-4">
            <span className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-500'} hidden sm:block`}>
              Last updated: {lastUpdated || 'loading...'}
            </span>
            <button
              onClick={toggleDarkMode}
              className={`text-xl p-2 rounded-full transition-all duration-200 ${
                isDarkMode ? 'hover:bg-gray-700 bg-gray-700' : 'hover:bg-gray-200 bg-gray-100'
              }`}
              title="Toggle dark mode"
            >
              {isDarkMode ? '☀️' : '🌙'}
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-4 py-8">
        {/* Mood Gauge Section */}
        <section className="text-center mb-12">
          <h2 className="text-3xl font-bold mb-3 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Today's Market Mood
          </h2>
          <p className={`${isDarkMode ? 'text-gray-400' : 'text-gray-600'} mb-8 text-lg`}>
            Based on latest Indian financial headlines
          </p>

          {/* Gauge Chart */}
          <div className="mb-6">
            <GaugeChart score={moodScore} />
          </div>

          {/* Sentiment Counts */}
          <div className="flex justify-center gap-8 text-sm mb-8">
            <div className="flex flex-col items-center p-4 rounded-lg bg-green-50 dark:bg-green-900 dark:bg-opacity-20">
              <span className="text-3xl mb-2">📈</span>
              <span className="font-bold text-green-600 text-2xl">{sentimentCounts.bullish}</span>
              <span className={isDarkMode ? 'text-gray-300' : 'text-gray-700'}>Bullish</span>
            </div>
            <div className="flex flex-col items-center p-4 rounded-lg bg-gray-50 dark:bg-gray-800">
              <span className="text-3xl mb-2">➡️</span>
              <span className={`font-bold text-2xl ${
                isDarkMode ? 'text-gray-400' : 'text-gray-600'
              }`}>{sentimentCounts.neutral}</span>
              <span className={isDarkMode ? 'text-gray-300' : 'text-gray-700'}>Neutral</span>
            </div>
            <div className="flex flex-col items-center p-4 rounded-lg bg-red-50 dark:bg-red-900 dark:bg-opacity-20">
              <span className="text-3xl mb-2">📉</span>
              <span className="font-bold text-red-600 text-2xl">{sentimentCounts.bearish}</span>
              <span className={isDarkMode ? 'text-gray-300' : 'text-gray-700'}>Bearish</span>
            </div>
          </div>

          {/* Refresh Button */}
          <button
            onClick={loadData}
            disabled={loading}
            className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 disabled:from-blue-400 disabled:to-purple-400 text-white font-medium px-8 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-3 mx-auto transform hover:scale-105 active:scale-95"
          >
            <span className={`text-xl ${loading ? 'animate-spin' : ''}`}>🔄</span>
            <span className="font-semibold">{loading ? 'Loading...' : 'Refresh Data'}</span>
          </button>
        </section>

        {/* Headlines Section */}
        <section>
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-2xl font-bold flex items-center gap-2">
              <span>📰</span>
              Latest Headlines
            </h3>
            <span className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-500'} bg-blue-100 dark:bg-blue-900 dark:bg-opacity-30 px-3 py-1 rounded-full`}>
              {headlines.length} headlines analyzed
            </span>
          </div>

          <div className="grid gap-6 md:grid-cols-2">
            {headlines.map((headline, index) => (
              <HeadlineCard key={index} headline={headline} />
            ))}
          </div>
        </section>

        {/* Footer */}
        <footer className={`text-center mt-16 py-8 border-t ${
          isDarkMode ? 'border-gray-700 text-gray-400' : 'border-gray-200 text-gray-500'
        }`}>
          <div className="flex justify-center items-center gap-2 mb-2">
            <span className="text-2xl">🚀</span>
            <p className="text-lg font-semibold">Market Mood Analyzer</p>
          </div>
          <p className="text-sm">Built with React & Tailwind CSS • Real-time sentiment analysis</p>
        </footer>
      </main>
    </div>
  );
};

export default App;
