import React, { useState, useEffect } from 'react';

const sampleHeadlines = Array.from({ length: 30 }).map((_, i) => ({
  title: `Market reacts to event ${i + 1}`,
  source: ['Economic Times', 'MoneyControl', 'Business Standard'][i % 3],
  url: '#',
  sentiment_score: (i % 3 - 1) * 0.3 + 0.1,
  sentiment_label: ['Bearish', 'Neutral', 'Bullish'][i % 3],
}));

const App = () => {
  const [headlines, setHeadlines] = useState([]);
  const [moodScore, setMoodScore] = useState(0);

  useEffect(() => {
    const bullish = sampleHeadlines.filter(h => h.sentiment_label === 'Bullish').length;
    const bearish = sampleHeadlines.filter(h => h.sentiment_label === 'Bearish').length;
    const total = sampleHeadlines.length;
    const score = ((bullish - bearish) / total) * 100;
    setMoodScore(score.toFixed(1));
    setHeadlines(sampleHeadlines);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 p-4 font-sans">
      <header className="text-center mb-8">
        <h1 className="text-4xl font-bold text-purple-700">ðŸ“Š Market Mood</h1>
        <p className="text-gray-600 mt-2">Sentiment analysis of 30 financial headlines</p>
        <div className="mt-4 bg-white p-4 rounded shadow w-64 mx-auto">
          <div className="text-xl font-semibold text-center">{moodScore} Market Mood</div>
          <div className="w-full h-2 bg-gray-200 rounded mt-2 overflow-hidden">
            <div
              className={`h-full rounded ${moodScore >= 0 ? 'bg-green-500' : 'bg-red-500'}`}
              style={{ width: `${Math.min(Math.abs(moodScore), 100)}%` }}
            ></div>
          </div>
        </div>
      </header>

      <main className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 max-w-6xl mx-auto">
        {headlines.map((h, i) => (
          <div
            key={i}
            className={`bg-white p-4 rounded shadow border-l-4 ${
              h.sentiment_label === 'Bullish'
                ? 'border-green-500'
                : h.sentiment_label === 'Bearish'
                ? 'border-red-500'
                : 'border-gray-400'
            }`}
          >
            <div className="flex justify-between text-sm text-gray-500">
              <span>{h.source}</span>
              <span
                className={`${
                  h.sentiment_label === 'Bullish'
                    ? 'text-green-600'
                    : h.sentiment_label === 'Bearish'
                    ? 'text-red-600'
                    : 'text-gray-600'
                }`}
              >
                {h.sentiment_label}
              </span>
            </div>
            <a href={h.url} className="block mt-2 font-semibold hover:underline text-lg">
              {h.title}
            </a>
            <div className="text-xs text-gray-400 mt-1">Score: {h.sentiment_score}</div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default App;
