// src/App.js
import React, { useState, useEffect, useCallback } from 'react';

// Moved sample data outside component to prevent infinite re-renders
const sampleHeadlinesData = [
  { title: 'Sensex surges 500 points on positive quarterly earnings', source: 'Economic Times', url: 'https://example.com/news/sensex-surge', sentiment_score: 0.7, sentiment_label: 'Bullish' },
  { title: 'Nifty touches new all-time high amid strong FII inflows', source: 'Business Standard', url: 'https://example.com/news/nifty-high', sentiment_score: 0.8, sentiment_label: 'Bullish' },
  { title: 'Banking stocks decline on concerns over loan growth slowdown', source: 'MoneyControl', url: 'https://example.com/news/banking-decline', sentiment_score: -0.6, sentiment_label: 'Bearish' },
  { title: 'IT sector shows mixed performance in Q3 results, uncertainty remains', source: 'Reuters India', url: 'https://example.com/news/it-mixed', sentiment_score: 0.1, sentiment_label: 'Neutral' },
  { title: 'Rupee strengthens against dollar as global risk appetite improves', source: 'LiveMint', url: 'https://example.com/news/rupee-strong', sentiment_score: 0.5, sentiment_label: 'Bullish' },
  { title: 'Auto stocks rally on strong festive season sales numbers', source: 'Financial Express', url: 'https://example.com/news/auto-rally', sentiment_score: 0.65, sentiment_label: 'Bullish' },
  { title: 'Pharma sector faces regulatory headwinds in key export markets', source: 'The Hindu BusinessLine', url: 'https://example.com/news/pharma-headwinds', sentiment_score: -0.45, sentiment_label: 'Bearish' },
  { title: 'Metal stocks gain as global commodity prices see an uptick', source: 'CNBC TV18', url: 'https://example.com/news/metal-gain', sentiment_score: 0.4, sentiment_label: 'Bullish' },
  { title: 'FMCG companies report resilient demand despite inflationary pressures', source: 'Times of India', url: 'https://example.com/news/fmcg-demand', sentiment_score: 0.3, sentiment_label: 'Neutral' },
  { title: 'Real estate sector sees continued momentum in housing sales', source: 'NDTV Profit', url: 'https://example.com/news/real-estate-momentum', sentiment_score: 0.55, sentiment_label: 'Bullish' },
  { title: 'Global cues weak, Asian markets trade lower on inflation fears', source: 'Bloomberg Quint', url: 'https://example.com/news/global-cues-weak', sentiment_score: -0.7, sentiment_label: 'Bearish' },
  { title: 'Government announces new PLI scheme for manufacturing boost', source: 'PIB India', url: 'https://example.com/news/pli-scheme', sentiment_score: 0.75, sentiment_label: 'Bullish' },
  { title: 'Oil prices surge past $90/barrel, stoking inflation worries', source: 'Reuters Global', url: 'https://example.com/news/oil-surge', sentiment_score: -0.65, sentiment_label: 'Bearish' },
  { title: 'Startup funding sees a slight dip in the last quarter', source: 'TechCrunch India', url: 'https://example.com/news/startup-funding-dip', sentiment_score: -0.2, sentiment_label: 'Neutral' },
  { title: 'RBI MPC likely to maintain status quo on interest rates', source: 'Economic Times Poll', url: 'https://example.com/news/rbi-mpc-statusquo', sentiment_score: 0.0, sentiment_label: 'Neutral' },
  { title: 'Infrastructure spending set to increase in upcoming budget', source: 'Business Today', url: 'https://example.com/news/infra-spending-budget', sentiment_score: 0.6, sentiment_label: 'Bullish' },
  { title: 'Retail inflation eases slightly but remains above comfort zone', source: 'MoSPI Data', url: 'https://example.com/news/retail-inflation-eases', sentiment_score: -0.1, sentiment_label: 'Neutral' },
  { title: 'Semiconductor shortage continues to impact auto production', source: 'Automotive News', url: 'https://example.com/news/semiconductor-shortage', sentiment_score: -0.5, sentiment_label: 'Bearish' },
  { title: 'Green energy stocks rally on new government policies', source: 'CleanTechnica', url: 'https://example.com/news/green-energy-rally', sentiment_score: 0.85, sentiment_label: 'Bullish' },
  { title: 'Indian exports show robust growth for the third consecutive month', source: 'Ministry of Commerce', url: 'https://example.com/news/exports-growth', sentiment_score: 0.7, sentiment_label: 'Bullish' },
  { title: 'Market volatility expected to continue in the short term', source: 'Market Analysts Inc.', url: 'https://example.com/news/market-volatility', sentiment_score: -0.3, sentiment_label: 'Bearish' },
  { title: 'Tech giants invest heavily in Indian AI and Cloud infrastructure', source: 'Economic Times Tech', url: 'https://example.com/news/tech-giants-investment', sentiment_score: 0.6, sentiment_label: 'Bullish' },
];

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    if (typeof window !== 'undefined') {
      const stored = localStorage.getItem('marketMoodDarkMode');
      if (stored !== null) {
        return stored === 'true';
      }
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });

  const [headlines, setHeadlines] = useState([]);
  const [moodScore, setMoodScore] = useState(0);
  const [sentimentCounts, setSentimentCounts] = useState({ bullish: 0, bearish: 0, neutral: 0 });
  const [lastUpdated, setLastUpdated] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const htmlElement = document.documentElement;
    if (isDarkMode) {
      htmlElement.classList.add('dark');
    } else {
      htmlElement.classList.remove('dark');
    }
    try {
      localStorage.setItem('marketMoodDarkMode', isDarkMode.toString());
    } catch (error) {
      console.warn('Could not save dark mode preference to localStorage:', error);
    }
  }, [isDarkMode]);

  const calculateMoodScore = useCallback((headlinesData) => {
    if (!headlinesData || headlinesData.length === 0) {
      setSentimentCounts({ bullish: 0, bearish: 0, neutral: 0 });
      return 0;
    }

    let bullishCount = 0;
    let bearishCount = 0;
    let neutralCount = 0;

    headlinesData.forEach((h) => {
      if (h.sentiment_label === 'Bullish') bullishCount++;
      else if (h.sentiment_label === 'Bearish') bearishCount++;
      else neutralCount++;
    });

    const total = headlinesData.length;
    const score = total > 0 ? ((bullishCount - bearishCount) / total) * 100 : 0;

    setSentimentCounts({ bullish: bullishCount, bearish: bearishCount, neutral: neutralCount });
    return Math.round(score);
  }, []);

const loadData = async () => {
  setLoading(true);
  try {
    const response = await fetch(
      `https://api.rss2json.com/v1/api.json?rss_url=https://news.google.com/rss?hl=en-IN&gl=IN&ceid=IN:en`
    );
    const data = await response.json();

    const formattedData = data.items.map((item) => ({
      title: item.title,
      source: new URL(item.link).hostname,
      url: item.link,
      sentiment_score: 0,
      sentiment_label: 'Neutral',
    }));

    const calculatedMoodScore = calculateMoodScore(formattedData);
    setHeadlines(formattedData);
    setMoodScore(calculatedMoodScore);
    setLastUpdated(new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
  } catch (error) {
    console.error('RSS fetch error:', error);
  } finally {
    setLoading(false);
  }
};


  const toggleDarkMode = () => {
    setIsDarkMode(prev => !prev);
  };

  const getSentimentStyles = (label) => {
    switch (label) {
      case 'Bullish':
        return {
          bg: 'bg-green-100 dark:bg-green-900 dark:bg-opacity-30',
          text: 'text-green-700 dark:text-green-400',
          border: 'border-green-500 dark:border-green-600',
          tagBg: 'bg-green-200 dark:bg-green-700',
          tagText: 'text-green-800 dark:text-green-200',
        };
      case 'Bearish':
        return {
          bg: 'bg-red-100 dark:bg-red-900 dark:bg-opacity-30',
          text: 'text-red-700 dark:text-red-400',
          border: 'border-red-500 dark:border-red-600',
          tagBg: 'bg-red-200 dark:bg-red-700',
          tagText: 'text-red-800 dark:text-red-200',
        };
      default:
        return {
          bg: 'bg-gray-100 dark:bg-gray-700 dark:bg-opacity-30',
          text: 'text-gray-700 dark:text-gray-400',
          border: 'border-gray-400 dark:border-gray-500',
          tagBg: 'bg-gray-200 dark:bg-gray-600',
          tagText: 'text-gray-800 dark:text-gray-200',
        };
    }
  };

  const GaugeChart = ({ score }) => {
    const normalizedScore = Math.max(-100, Math.min(100, score));
    const angle = ((normalizedScore + 100) / 200) * 180;

    let moodText = 'Neutral';
    let moodTextColor = 'text-gray-600 dark:text-gray-400';
    let progressColor = isDarkMode ? 'rgb(75 85 99)' : 'rgb(107 114 128)';

    if (score > 25) {
      moodText = 'Very Bullish';
      moodTextColor = 'text-green-600 dark:text-green-400';
      progressColor = 'rgb(16 185 129)';
    } else if (score > 0) {
      moodText = 'Bullish';
      moodTextColor = 'text-green-500 dark:text-green-400';
      progressColor = 'rgb(16 185 129)';
    } else if (score < -25) {
      moodText = 'Very Bearish';
      moodTextColor = 'text-red-600 dark:text-red-400';
      progressColor = 'rgb(239 68 68)';
    } else if (score < 0) {
      moodText = 'Bearish';
      moodTextColor = 'text-red-500 dark:text-red-400';
      progressColor = 'rgb(239 68 68)';
    }

    return (
      // Increased height of the container slightly
      <div className="relative w-72 h-[170px] mx-auto"> {/* Adjusted height from h-40 to h-[170px] */}
        <svg viewBox="0 0 200 100" className="w-full h-full transform ">
          <path
            d="M 20 90 A 70 70 0 0 1 180 90"
            fill="none"
            stroke={isDarkMode ? '#374151' : '#e5e7eb'}
            strokeWidth="20"
            strokeLinecap="round"
          />
          {score !== 0 && (
            <path
              d="M 20 90 A 70 70 0 0 1 180 90"
              fill="none"
              stroke={progressColor}
              strokeWidth="20"
              strokeLinecap="round"
              style={{
                strokeDasharray: Math.PI * 70,
                strokeDashoffset: Math.PI * 70 * (1 - (angle / 180)),
                transition: 'stroke-dashoffset 0.7s ease-out'
              }}
             />
          )}
          <line
            x1="100"
            y1="90"
            x2="100"
            y2="20"
            stroke={isDarkMode ? '#a0aec0' : '#4a5568'}
            strokeWidth="3"
            strokeLinecap="round"
            className="gauge-needle"
            style={{
              transformOrigin: '100px 90px',
              transform: `rotate(${angle - 90}deg)`,
            }}
          />
          <circle cx="100" cy="90" r="6" fill={isDarkMode ? '#cbd5e1' : '#2d3748'} />
        </svg>

        {/* Adjusted positioning of the text block slightly lower to avoid overlap */}
        <div className="absolute bottom-[5px] left-1/2 transform -translate-x-1/2 text-center w-full"> {/* Adjusted from bottom-0 */}
          <div className={`text-4xl font-bold ${moodTextColor}`}>
            {score >= 0 ? `+${score}` : score}
          </div>
          <div className={`text-md font-medium ${moodTextColor} uppercase tracking-wider`}>
            {moodText}
          </div>
        </div>
      </div>
    );
  };

  const HeadlineCard = ({ headline }) => {
    const styles = getSentimentStyles(headline.sentiment_label);
    const sentimentEmoji =
      headline.sentiment_label === 'Bullish' ? '📈' :
      headline.sentiment_label === 'Bearish' ? '📉' :
      '➡️';

    return (
      <div
        className={`border-l-4 ${styles.border} ${isDarkMode ? 'bg-dark-card hover:bg-gray-700' : 'bg-light-card hover:bg-gray-50'} 
                    rounded-lg p-5 shadow-lg hover:shadow-xl transition-all duration-300 group animate-fadeIn flex flex-col justify-between`}
      >
        <div>
          <div className="flex justify-between items-start mb-3">
            <span className={`text-xs font-medium ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'}`}>
              {headline.source}
            </span>
            <span
              className={`text-xs font-semibold ${styles.tagText} ${styles.tagBg} px-2.5 py-1 rounded-full`}
            >
              {headline.sentiment_label}
            </span>
          </div>
          <h4 className="text-lg font-semibold mb-2 leading-snug "> {/* Removed group-hover from h4, will apply directly to a if present */}
            {headline.url && headline.url !== '#' ? (
              <a
                href={headline.url}
                target="_blank"
                rel="noopener noreferrer"
                className="hover:text-brand-primary dark:hover:text-blue-400 focus:outline-none focus:ring-2 focus:ring-brand-primary dark:focus:ring-blue-500 rounded transition-colors duration-200"
              >
                {headline.title}
              </a>
            ) : (
              headline.title
            )}
          </h4>
        </div>
        <div className={`text-sm ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'} flex justify-between items-center mt-auto pt-2`}>
          <span>Score: <span className={`font-bold ${styles.text}`}>{headline.sentiment_score}</span></span>
          <span className="text-2xl">{sentimentEmoji}</span>
        </div>
      </div>
    );
  };

  const SentimentStatCard = ({ icon, label, count, colorClass, bgColorClass }) => (
    <div className={`flex flex-col items-center justify-center p-5 rounded-xl shadow-md
                   ${isDarkMode ? 'bg-dark-card' : bgColorClass} 
                   w-full sm:w-auto min-w-[120px] transition-all duration-300`}
    >
      <span className="text-4xl mb-2">{icon}</span>
      <span className={`font-bold text-3xl mb-1 ${colorClass}`}>{count}</span>
      <span className={`text-sm font-medium ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'}`}>{label}</span>
    </div>
  );
useEffect(() => {
  loadData();
}, [loadData]);
  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-dark-bg text-dark-text' : 'bg-light-bg text-light-text'}`}>
      <nav className={`${isDarkMode ? 'bg-dark-card' : 'bg-light-card'} shadow-lg sticky top-0 z-50 border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            <div className="flex-shrink-0 flex items-center gap-3">
              <span className="text-4xl">📊</span>
              <span className="text-3xl font-bold gradient-text bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500">
                Market Mood
              </span>
            </div>
            <div className="flex items-center gap-4">
              <span className={`text-sm ${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'} hidden md:block`}>
                Last updated: {loading ? 'Updating...' : lastUpdated || 'N/A'}
              </span>
              <button
                onClick={toggleDarkMode}
                className={`p-2.5 rounded-full transition-all duration-300 focus:outline-none focus:ring-2
                            ${isDarkMode ? 'bg-gray-700 hover:bg-gray-600 text-yellow-400 focus:ring-yellow-500' 
                                         : 'bg-gray-200 hover:bg-gray-300 text-purple-600 focus:ring-purple-500'}`}
                title="Toggle Dark Mode"
              >
                {isDarkMode ? '☀️' : '🌙'}
              </button>
            </div>
          </div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
        <section className="mb-16 text-center">
          <h2 className="text-4xl font-extrabold mb-4 gradient-text bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600">
            Today's Market Pulse
          </h2>
          <p className={`${isDarkMode ? 'text-dark-text-secondary' : 'text-light-text-secondary'} mb-10 text-lg max-w-2xl mx-auto`}>
            Sentiment analysis based on the latest financial headlines from India.
          </p>

          <div className="mb-8">
            {loading ? (
              <div className="w-72 h-[170px] mx-auto flex items-center justify-center"> {/* Adjusted height to match gauge */}
                <div className="animate-spinSlow rounded-full h-16 w-16 border-t-4 border-b-4 border-brand-primary dark:border-blue-400"></div>
              </div>
            ) : (
              <GaugeChart score={moodScore} />
            )}
          </div>

          <div className="flex flex-col sm:flex-row justify-center items-stretch gap-4 sm:gap-6 mb-10">
            <SentimentStatCard icon="📈" label="Bullish" count={sentimentCounts.bullish} colorClass="text-green-600 dark:text-green-400" bgColorClass="bg-green-50" />
            <SentimentStatCard icon="➡️" label="Neutral" count={sentimentCounts.neutral} colorClass="text-gray-600 dark:text-gray-400" bgColorClass="bg-gray-50" />
            <SentimentStatCard icon="📉" label="Bearish" count={sentimentCounts.bearish} colorClass="text-red-600 dark:text-red-400" bgColorClass="bg-red-50" />
          </div>

          <button
            onClick={loadData}
            disabled={loading}
            className="px-8 py-3.5 text-lg font-semibold rounded-lg shadow-lg
                       bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 text-white
                       hover:from-blue-700 hover:via-purple-700 hover:to-pink-700
                       focus:outline-none focus:ring-4 focus:ring-purple-300 dark:focus:ring-purple-800
                       disabled:opacity-60 disabled:cursor-not-allowed
                       transition-all duration-300 transform hover:scale-105 active:scale-95
                       flex items-center gap-3 mx-auto"
          >
            <span className={`text-xl ${loading ? 'animate-spin' : ''}`}>🔄</span>
            <span>{loading ? 'Analyzing...' : 'Refresh Pulse'}</span>
          </button>
        </section>

        <section className="mb-12">
          <div className="flex flex-col sm:flex-row justify-between items-center mb-8 pb-2 border-b-2 border-dashed dark:border-gray-700 border-gray-300">
            <h3 className="text-3xl font-bold flex items-center gap-3 mb-4 sm:mb-0">
              <span>📰</span>
              Latest Headlines
            </h3>
            <span className={`text-sm font-medium px-3 py-1.5 rounded-full ${isDarkMode ? 'bg-blue-900 bg-opacity-50 text-blue-300' : 'bg-blue-100 text-blue-700'}`}>
              {loading ? '...' : headlines.length} articles analyzed
            </span>
          </div>

          {loading && headlines.length === 0 ? (
             <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(6)].map((_, i) => (
                  <div key={i} className={`rounded-lg p-5 shadow-lg ${isDarkMode ? 'bg-dark-card' : 'bg-light-card'} h-full flex flex-col`}>
                    <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-1/4 mb-3 animate-pulse"></div>
                    <div className="h-6 bg-gray-300 dark:bg-gray-600 rounded w-3/4 mb-4 animate-pulse"></div>
                    <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-full mb-2 animate-pulse"></div>
                    <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-5/6 animate-pulse"></div>
                    <div className="flex justify-between items-center mt-auto pt-2">
                      <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-1/3 animate-pulse"></div>
                      <div className="h-8 w-8 bg-gray-300 dark:bg-gray-600 rounded-full animate-pulse"></div>
                    </div>
                  </div>
                ))}
            </div>
          ) : headlines.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {headlines.map((headline, index) => (
                <HeadlineCard key={index} headline={headline} />
              ))}
            </div>
          ) : (
            !loading && <p className="text-center text-lg text-gray-500 dark:text-gray-400 py-8">No headlines found. Try refreshing.</p>
          )}
        </section>

        <footer className={`text-center mt-20 py-10 border-t ${isDarkMode ? 'border-gray-700 text-dark-text-secondary' : 'border-gray-200 text-light-text-secondary'}`}>
          <div className="flex justify-center items-center gap-2 mb-3">
            <span className="text-3xl">🚀</span>
            <p className="text-xl font-semibold">Market Mood Analyzer</p>
          </div>
          <p className="text-sm">
            Crafted with React & Tailwind CSS for insightful sentiment snapshots.
          </p>
          <p className="text-xs mt-1">
            Data is for illustrative purposes only. Not financial advice. Links are placeholder URLs.
          </p>
        </footer>
      </main>
    </div>
  );
};

export default App;
