// src/App.js
import React, { useState, useEffect, useCallback } from 'react';
// You might need a sentiment analysis library if the API doesn't provide it
// import Sentiment from 'sentiment'; // Example: npm install sentiment

// Moved sample data outside - NO LONGER NEEDED if fetching real news
// const sampleHeadlinesData = [...];

const API_KEY = 'YOUR_NEWSAPI_ORG_API_KEY'; // !!! REPLACE WITH YOUR ACTUAL API KEY !!!

// Function to perform basic sentiment analysis (if API doesn't provide it)
// const getSentimentFromTitle = (title) => {
//   const sentimentAnalyzer = new Sentiment();
//   const result = sentimentAnalyzer.analyze(title);
//   let label = 'Neutral';
//   if (result.score > 0.5) label = 'Bullish'; // Adjust thresholds as needed
//   else if (result.score < -0.5) label = 'Bearish';
//   return { score: result.score, label };
// };

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(() => { /* ... existing code ... */ });
  const [headlines, setHeadlines] = useState([]);
  const [moodScore, setMoodScore] = useState(0);
  const [sentimentCounts, setSentimentCounts] = useState({ bullish: 0, bearish: 0, neutral: 0 });
  const [lastUpdated, setLastUpdated] = useState('');
  const [loading, setLoading] = useState(true);

  // ... existing useEffect for dark mode ...

  const calculateMoodScore = useCallback((headlinesData) => {
    // ... existing code ...
    // Ensure headlinesData has 'sentiment_label'
    if (!headlinesData || headlinesData.length === 0 || !headlinesData[0].sentiment_label) {
        setSentimentCounts({ bullish: 0, bearish: 0, neutral: 0 });
        return 0;
    }
    // ... rest of your existing calculateMoodScore logic
    let bullishCount = 0;
    let bearishCount = 0;
    let neutralCount = 0;

    headlinesData.forEach((h) => {
      if (h.sentiment_label === 'Bullish') bullishCount++;
      else if (h.sentiment_label === 'Bearish') bearishCount++;
      else neutralCount++;
    });

    const total = headlinesData.length;
    const score = total > 0 ? ((bullishCount - bearishCount) / total) * 100 : 0;

    setSentimentCounts({ bullish: bullishCount, bearish: bearishCount, neutral: neutralCount });
    return Math.round(score);
  }, []);

  const loadData = useCallback(async () => {
    setLoading(true);
    if (API_KEY === 'YOUR_NEWSAPI_ORG_API_KEY') {
        console.error("Please replace 'YOUR_NEWSAPI_ORG_API_KEY' with your actual NewsAPI key.");
        setHeadlines([{ title: "API Key Missing. Please add your NewsAPI key in App.js.", source: "App Alert", url: "#", sentiment_score: 0, sentiment_label: 'Neutral' }]);
        setMoodScore(0);
        setSentimentCounts({ bullish: 0, bearish: 0, neutral: 0 });
        setLoading(false);
        return;
    }

    try {
